mapper.select:
SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,TOTAL_CNT ,C.CODE_NAME AS BOARD_TYPE_KR FROM ( SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT FROM BOARD
WHERE BOARD_TYPE IN
#{condition}
) B LEFT JOIN COM_CODE C ON B.BOARD_TYPE = C.CODE_ID WHERE NUMROW BETWEEN (#{pageNo}-1)*10 + 1 AND (#{pageNo})*10

mapper.update:
    	UPDATE 
        BOARD
        SET
	        BOARD_TITLE = #{boardTitle},
	        BOARD_COMMENT = #{boardComment}
        WHERE 	BOARD_TYPE = #{boardType}
    	AND 	BOARD_NUM  = #{boardNum}

mapper.delete:
DELETE FROM
BOARD
WHERE 	BOARD_TYPE = #{boardType}
AND 	BOARD_NUM  = #{boardNum}
